<template>
  <div class="container">
    <div class="row justify-content-center">
      <div v-show="!isWorking" class="col-sm-12 col-md-6 col-lg-4">
        <div class="jumbotron text-center">
          <div class="container">
            <img class="img-fluid rounded" src="../../../assets/pushups.png" alt="">
            <h2>Push-ups</h2>
            <p class="lead">
              Description: lorem ipsum
            </p>
          </div>
        </div>
      </div>
      <div class="col-sm-12 col-md-6 col-lg-8">
        <count-down-timer @finished="togglePomodoro" :time="time"></count-down-timer>
      </div>
    </div>
  </div>
</template>
<script>
  import CountDownTimer from './timer/CountDownTimer'
  import { mapGetters } from 'vuex'

  // courtesy of https://stackoverflow.com/a/23395136
  function beep () {
    var snd = new Audio('data:audio/wav;base64,')
    snd.play()
  }

  export default {
    data () {
      return {
        isWorking: true,
        isShortBreak: false,
        isLongBreak: false,
        pomodoros: 0
      }
    },
    computed: {
      ...mapGetters({
        config: 'getConfig'
      }),
      time () {
        let minutes
        if (this.isWorking) {
          minutes = this.config.workingPomodoro
        } else if (this.isShortBreak) {
          minutes = this.config.shortBreak
        } else if (this.isLongBreak) {
          minutes = this.config.longBreak
        }

        return minutes * 60
      }
    },
    components: {
      CountDownTimer
    },
    methods: {
      togglePomodoro () {
        beep()
        // toggle working state
        this.isWorking = !this.isWorking

        // reset break states
        this.isShortBreak = this.isLongBreak = false

        // we have switched to the working state, just return
        if (this.isWorking) {
          return
        }

        // we have switched to the break state, increase the number of pomodoros and choose between long and short break
        this.pomodoros ++
        this.isLongBreak = this.pomodoros % this.config.pomodorosTillLongBreak === 0
        this.isShortBreak = !this.isLongBreak
      }
    }
  }
</script>
